function printTable( map )
	echo("-------------------------------------------------------\n");
	for k, v in pairs( map ) do
		if type(v) ~= "function" and type(v) ~= "table" then 
			echo( "key=" .. k .. "  			value=" .. v .. "\n" )
		end
	end
	echo("-------------------------------------------------------\n");
end

printTable(_G)

