function initializeFlags()
--defensesEnabled = false combatLoaded = false corpselocation = "" classchange = false serverside = true dead = false siphealth = -1 sipmana = -1 sipbalance = true fullwillandmana = true mossbalance = true magirobes = false mossenabled = true manathreshold = 1 reckless_check = 0 currentclass = myclass() or "blademaster" --siphealthfor = 0.20 healthaffsabove = tonumber(settings.healthaffsabove) sippriority = settings.sippriority manadanger = 4515 - (4515 * 0.25) healthdanger = 4784 - (4784 * 0.25) mindseye = mindseye or false preppoint = 4 lustlist = {} predictions = {} groupedByArea = {} Enemy_Balance_Data = {}
target = "none"
-- Reset the limb tracking variables, but only if they are not already initialized limbdamage = {}
slainPlayers = {} -- .json api newsfeed table. nowparrying = "none" curingset = settings.curingset DEBUG_MODE = settings.debug cmdsep = settings.cmdsep class = settings.myclass cureMethod = settings.cureMethod batching = settings.batching showGauges = settings.showGauges gagInventory = settings.gagInventory showScrollbar = settings.showScrollbar promptEnabled = settings.promptEnabled promptPercentages = settings.promptPercentages end

function initializeVitals()
  vitals =
    {
      health =
        {
          current = tonumber(gmcp.Char.Vitals.hp),
          previous = tonumber(gmcp.Char.Vitals.hp),
          halflife = tonumber(gmcp.Char.Vitals.maxhp) * 0.50,
        },
      maxHealth = tonumber(gmcp.Char.Vitals.maxhp),
      mana =
        {
          current = tonumber(gmcp.Char.Vitals.mp),
          previous = tonumber(gmcp.Char.Vitals.mp),
          halflife = tonumber(gmcp.Char.Vitals.maxmp) * 0.50,
        },
      maxMana = tonumber(gmcp.Char.Vitals.maxmp),
      ep =
        {
          current = tonumber(gmcp.Char.Vitals.ep),
          previous = tonumber(gmcp.Char.Vitals.ep),
          halflife = tonumber(gmcp.Char.Vitals.maxep) * 0.50,
        },
      maxEp = tonumber(gmcp.Char.Vitals.maxep),
      wp =
        {
          current = tonumber(gmcp.Char.Vitals.wp),
          previous = tonumber(gmcp.Char.Vitals.wp),
          halflife = tonumber(gmcp.Char.Vitals.maxwp) * 0.50,
        },
      maxWp = tonumber(gmcp.Char.Vitals.maxwp),
      rank = {current = tonumber(string.cut(gmcp.Char.Status.xp, 2)), previous = tonumber(string.cut(gmcp.Char.Status.xp, 2)), halflife = 50},
      maxRank = 100,
    }
  interpolationState = {health = false, mana = false, ep = false, wp = false, rank = false}
end

