function initializeSelfPrecache()
  if cureMethod == "herbal" then
    precache = {ginseng = 0, kelp = 0, goldenseal = 0, bloodroot = 0}
    desired_precache = {ginseng = 3, kelp = 3, goldenseal = 3, bloodroot = 3}
  else
    precache = {ferrum = 0, aurum = 0, plumbum = 0, magnesium = 0}
    desired_precache = {ferrum = 3, aurum = 3, plumbum = 3, magnesium = 3}
  end
end

function initializePipeSettings()
  pipes = {
    refill = 3
}
end

-- Add cure or defense to the pipeline
function initializePipeLine()
  pipeline = {defenses={},afflictions={}}
end

function initializeVitalsData() vitals = { health = {current = 0, previous = 0, halflife = 0}, maxHealth = 0, mana = {current = 0, previous = 0, halflife = 0}, maxMana = 0, ep = {current = 0, previous = 0, halflife = 0}, maxEp = 0, wp = {current = 0, previous = 0, halflife = 0}, maxWp = 0, rank = {current = 0, previous = 0, halflife = 0}, maxRank = 0, }
interpolationState = { health = false, mana = false, ep = false, wp = false, rank = false }
end

