  balance_start_time = 0
  equilibrium_start_time = 0
  stunned_start_time = 0
  balance_data = {
      free = {
          in_use = false,
          avg_time = 99,
          aff_modifiers = { lethargy = { multiplier = 1.0, offset = 0.5 } },
