  Geyser.MiniConsole:new(  
    {name = "roomwindow.miniconsole", x = 10, y = 53, width = "95.85%", height = "86%"},
    GUI.roomwindow
  )
setWindowWrap("roomwindow.miniconsole", 60)
setBackgroundColor("roomwindow.miniconsole", 0, 0, 0, 200) -- Black with transparency
