neraeos = { tritoncoinsilver = {required=1, short="silver"}, tritoncoinsalt = {required=1, short="salt"}, tritoncoinmark = {required=1, short="mark"}, tritoncoinscript = {required=1, short="script"}, }, aurora = { ethiantearradiance = {required=1, short="radiance"}, ethiantearstone = {required=1, short="stone"}, ethiantearlight = {required=1, short="light"}, ethiantearstrata = {required=1, short="strata"}, }, babel = { lightlessclothstrip = {required=1, short="strip"}, lightlesscloththread = {required=1, short="thread"}, lightlessclothpattern = {required=1, short="pattern"}, lightlessclothcorruption = {required=1, short="corruption"}, }, deucalion = { deucalioncloththread = {required=1, short="thread"}, deucalionclothsmoke = {required=1, short="smoke"}, deucalionclothcloth = {required=1, short="cloth"}, deucalionclothenchantment = {required=1, short="feathers"}, }, pandora = { glasseyeiris = {required=1, short="iris"}, glasseyeball = {required=1, short="ball"}, glasseyelight = {required=1, short="light"}, glasseyepupil = {required=1, short="pupil"}, }, sartan = { stalagmitefangspan = {required=1, short="span"}, stalagmitefangpoint = {required=1, short="point"}, stalagmitefangblood = {required=1, short="blood"}, stalagmitefangcarvings = {required=1, short="carvings"}, }, ourania = { cometbarbblade = {required=1, short="blade"}, cometbarbglyphs = {required=1, short="glyphs"}, cometbarbdarkness = {required=1, short="darkness"}, cometbarbsigils = {required=1, short="sigils"}, }, tlalaiad = { shadowcapsuletop = {required=1, short="top"}, shadowcapsulebase = {required=1, short="base"}, shadowcapsulemechanism = {required=1, short="mechanism"}, shadowcapsuleanomaly = {required=1, short="anomaly"}, },
-- Logosmas snowball = { snowballsnow = {required=1, short="snow"}, snowballreform = {required=1, short="reform"}, snowballreturn = {required=1, short="return"}, snowballbinding = {required=1, short="binding"}, }, halfnog = { halfnogpot = {required = 1, short="pot"}, halfnogbrew = {required = 1, short="brew"}, halfnogladle = {required = 1, short="ladle"}, halfnoghandle = {required = 1, short="handle"}, }, popcorngarland = { garlandcorn = {required = 1, short="corn"}, garlandsilk = {required = 1, short="silk"}, garlandglitter = {required = 1, short="glitter"}, garlandspell = {required = 1, short="spell"}, }, taffy = { taffysugar = {required = 1, short="sugar"}, taffyspecial = {required = 1, short="special"}, taffymagic = {required = 1, short="magic"}, taffybag = {required = 1, short="bag"}, }, snowglobe = { snowglobehouse = {required = 1, short="house"}, snowglobebell = {required = 1, short="bell"}, snowglobesnow = {required = 1, short="snow"}, snowglobeglass = {required = 1, short="glass"}, }, logosmasscarf = { redscarfwool = {required = 1, short="red"}, goldscarfwool = {required = 1, short="gold"}, scarfthread = {required = 1, short="thread"}, scarfcrystals = {required = 1, short="crystals"}, }, snowsack = { snowsackcloth = {required = 1, short="cloth"}, snowsacksnow = {required = 1, short="snow"}, snowsackstring = {required = 1, short="string"}, snowsackicicle = {required = 1, short="icicle"}, }, gifttag = { gifttaglabel = {required = 1, short="label"}, gifttagwriting = {required = 1, short="writing"}, gifttageyelet = {required = 1, short="eyelet"}, gifttagribbon = {required = 1, short="ribbon"}, }, }
talisman.help = function () local name if gmcp then name = gmcp.Char.Status.name:upper() else name = "SERAGORN" end cecho("<white>USEAGE:\n") cecho("<white>TLIST - <reset>Displays a summary list of all known talisman pieces.\n") cecho("<white>TCOMPLETE <talisman>- <reset>Splits out the required pieces and completes the given talisman\n") cecho("<reset> - Example: TCOMPLETE AZATLANKNIFE\n") cecho("<white>TTRADE <adventurer> <piece> [<count>]\n") cecho("<reset> - Used to trade <count> (defaults to 1) pieces of <piece> to <adventurer>\n") cecho("<reset> - Example: TTRADE "..name.." bluedragonscale 20\n") cecho("<reset> - Note: aliases only work after talisman pieces has been checked the first time.\n") cecho("<reset> - After this point it should keep an accurate list of pieces automatically.\n") cecho("<white>TTRADE SET <adventurer> <setname>\n") cecho("<reset> - Used to trade all the pieces required to complete <set> to <adventurer>\n") cecho("<reset> - Example: TTRADE SET DRAGON "..name.."\n") cecho("<white>TTRADE TALISMAN <adventurer> <talismanname>\n") cecho("<reset> - Used to trade all the pieces required to complete a specific <talisman> to <adventurer>\n") cecho("<reset> - Example: TTRADE TALISMAN TRANSOCEANIC "..name.."\n") cecho("\n<white>IMPORTANT: No validation is performed on trading complete sets/talismans.\n") cecho("<white>This means if you trade an incomplete set you will send some but not all of the pieces.\n") end
talisman.echo = function (text) cecho(string.format("<%s>(Talisman): %s\n", talisman.settings.color, text)) end
talisman.reset = function () talisman.pieces = {} end
talisman.getkey = function (longname) return talisman.names[longname] end
talisman.setisactive = function (set) for i,_ in pairs(talisman.sets[set]) do if talisman.talismanisactive(i) then return true end end return false end
talisman.talismanisactive = function (talismanname) for k,d in pairs(talisman.info[talismanname]) do if talisman.pieces[k] then for i,v in pairs(talisman.pieces[k]) do if (tonumber(v) or 0)>0 then return true end end end end return false end
talisman.complete = function (talismanname) if not talisman.cancomplete(talismanname) then talisman.echo(string.format("Cannot complete talisman %s, pieces missing.", talismanname)) return end
for k,d in pairs(talisman.info[talismanname]) do local required = (math.log(d.required)/math.log(2))+1 if not talisman.make(k, required) then talisman.echo(string.format("Error: Could not make level %d of %s, please check talisman pieces to correct stored data.", (tonumber(required) or -1), k)) return end talisman.remove(k, required, 1) end send(string.format("Talisman complete %s", talismanname)) end
talisman.cancomplete = function (talismanname) if not talisman.info[talismanname] then talisman.echo(string.format("Invalid talisman %s", talismanname)) return false end
for k,d in pairs(talisman.info[talismanname]) do if talisman.getcount(k) < d.required then -- dont have required pieces! return false end end return true end
talisman.tradeset = function (who, set) if not talisman.sets[set] then talisman.echo(string.format("Invalid talisman set %s", set)) return end for i,_ in pairs(talisman.sets[set]) do talisman.tradetalisman(who, i) end end
talisman.tradetalisman = function (who, talismanname) if not talisman.info[talismanname] then talisman.echo(string.format("Invalid talisman %s", talismanname)) return end
for k,d in pairs(talisman.info[talismanname]) do talisman.trade(who, k, d.required) end end
talisman.announce = function (piecename, size) local count = talisman.getcount(piecename) if size >= 0 then count = count + 2^(math.abs(tonumber(size))-1) else count = count - 2^(math.abs(tonumber(size))-1) end cecho(string.format("<%s> (Count: %d)", talisman.settings.color, count)) end
talisman.getcount = function (piecename) local count = 0 if not talisman.pieces[piecename] then return 0 end
for x,y in pairs(talisman.pieces[piecename]) do --x = level, y = count if (tonumber(y) or 0) > 0 then count = count + (2^(tonumber(x)-1))*tonumber(y) end end return count end
talisman.getnextsmallest = function (piecename, level) local nextpiece
if not talisman.pieces[piecename] then return end
for i,v in pairs(talisman.pieces[piecename]) do if tonumber(i)==level and tonumber(v)>0 then -- shouldnt happen, we shouldnt need to split something we already have.. dont keep looping.. talisman.echo(string.format("Why auto split %s %d when we already have it?", piecename, level)) return tonumber(i) elseif tonumber(i)>level and tonumber(v)>0 and tonumber(i)<(nextpiece or 99) then -- this piece is the current 'next smallest bigger piece' -- Note: this breaks if talisman pieces start accumulating to be level 100 (which is 2^99 total pieces, good luck) nextpiece = tonumber(i) end end if not nextpiece then talisman.echo(string.format("Could not find next size to split for %s %d!", piecename, level)) end return nextpiece end
talisman.make = function (piecename, level) talisman.pieces[piecename] = talisman.pieces[piecename] or {} talisman.pieces[piecename][tostring(level)] = talisman.pieces[piecename][tostring(level)] or 0
if talisman.pieces[piecename][tostring(level)]>0 then -- we already have one of these.. return true so we can do the trade (if thats where we came from) return true else -- we need to split one down.. -- start moving up until we find the next smallest one we have thats bigger than what we need.. local i = talisman.getnextsmallest(piecename, level) if not i then return false end -- we couldnt find one bigger, this is an error condition.. for x=i,(level+1),-1 do send(string.format("talisman split %s %d", piecename, x)) talisman.pieces[piecename][tostring(x-1)] = (talisman.pieces[piecename][tostring(x-1)] or 0) + 2 talisman.pieces[piecename][tostring(x)] = (talisman.pieces[piecename][tostring(x)] or 0) -1 -- do the maths here, increment smaller pieces, remove bigger ones, etc.. -- make sure we dont trigger auto combining tho! end return true end end
talisman.trade = function (who, piecename, quantity, price) local count = talisman.getcount(piecename) if count<quantity then talisman.echo(string.format("Only %d available of %s ", count, piecename)) return end if count>256 then talisman.echo("Trade alias can only handle quantities up to 256, sorry!") return end local required = {} local remaining = quantity local index = 9 while index>0 and remaining>0 do if remaining>=(2^(index-1)) then required[tostring(index)]=true remaining=remaining-(2^(index-1)) end index=index-1 end for i,v in pairs(required) do if not talisman.make(piecename, tonumber(i)) then talisman.echo(string.format("Error: Could not make level %d of %s", tonumber(i), piecename)) return else if not price then send(string.format("Talisman trade piece %s %d to %s for nothing", piecename, tonumber(i), who)) else send(string.format("Talisman trade piece %s %d to %s for %d", piecename, tonumber(i), who)) end talisman.remove(piecename, tonumber(i), 1) end end end
talisman.add = function (piecename, level, count, type) local name = talisman.lookup[piecename:cut(20)]
if not name then cecho("<red>***") return end
talisman.pieces[name] = talisman.pieces[name] or {} talisman.pieces[name][tostring(level)] = talisman.pieces[name][tostring(level)] or 0 talisman.pieces[name][tostring(level)] = talisman.pieces[name][tostring(level)] + (tonumber(count) or 1)
while talisman.pieces[name][tostring(level)] > 1 do send(string.format("talisman combine %s %s", name, tostring(level))) talisman.pieces[name][tostring(level)] = talisman.pieces[name][tostring(level)]-2 talisman.add(name, tonumber(level)+1, 1) end end
talisman.remove = function (piecename, level, count) if not talisman.pieces[piecename] then return end if not talisman.pieces[piecename][tostring(level)] then return end
if tonumber(talisman.pieces[piecename][tostring(level)])<=tonumber(count) then talisman.pieces[piecename][tostring(level)]=0 else talisman.pieces[piecename][tostring(level)]=talisman.pieces[piecename][tostring(level)]-tonumber(count) end end
talisman.rightpad = function (data, length) return string.cut(string.format("%s%s", (data or "?"), string.rep(" ", length)), length) end
talisman.highlight = function () selectCurrentLine() fg("gold") resetFormat() end
talisman.summary = function (setname) local talismangrandtotal = 0 if setname then if not talisman.setisactive(setname) then talisman.echo(string.format("Set Inactive %s", setname)) return end talisman.displayset(setname) return end for i,v in ipairs(talisman.setorder) do talisman.settotal = 0 talisman.setspare = 0 talisman.setrequired = 0 if talisman.setisactive(v) then talisman.displayset(v) talismangrandtotal = talismangrandtotal + talisman.setcredittotal end end cecho(string.format("<white>Total Value: <red>%d (%d)<white>\n\n", talismangrandtotal,((talismangrandtotal or 0) + (tonumber(gmcp.Char.Status.boundcredits) or 0) + (tonumber(gmcp.Char.Status.unboundcredits) or 0)))) end
talisman.displayset = function (set) if not talisman.sets[set] then talisman.echo(string.format("Invalid talisman set for output %s", set)) return end
talisman.settotal = 0 cechoLink(string.format("<white>%s Set<reset>\n", set:title()), [[printCmdLine("ttrade set ]]..(target or "<person>").." "..set..[[")]], "Click to trade complete set", true) cecho(string.format("<white>%s<reset>\n", string.rep("-", string.len(set)+4)))
talisman.setcredittotal = 0 if talisman.setformat[set] and talisman.setformat[set].order then -- echo the header.. for _,v in ipairs(talisman.setformat[set].order) do if talisman.talismanisactive(v) then talisman.displaysetdetail(set, v, talisman.sets[set][v]) end end else for i,v in pairs(talisman.sets[set]) do if talisman.talismanisactive(i) then talisman.displaysetdetail(set, i, v) end end end if talisman.setrequired > 0 then cecho(string.format("<white>Total: %d of %d common (%d%% plus %d spare) <red>(%s)<white>\n\n", talisman.settotal, talisman.setrequired, math.floor(talisman.settotal*100/talisman.setrequired), talisman.setspare, (talisman.setcredittotal or 0))) end end
talisman.displaysetdetail = function (set, talismanname, talismandata)
local talismanfull, talismancount talisman.talismantotal = 0 talisman.talismanspare = 0 talisman.talismanrequired = 0 cechoLink(string.format("<%s>%s", ((talismandata and talismandata.color) or talisman.settings.color), talisman.rightpad(talismandata.short, talisman.setformat[set].titlewidth)), [[printCmdLine("ttrade talisman ]]..(target or "<person>").." "..talismanname..[[")]], "Click to trade complete talisman", true) talismanfull, talismancount = talisman.displaytalisman(talismanname, set, talismandata.order) talisman.settotal = talisman.settotal + talisman.talismantotal talisman.setspare = talisman.setspare + talisman.talismanspare talisman.setrequired = talisman.setrequired + talisman.talismanrequired if talisman.talismanrequired-talisman.talismantotal>0 then cecho(string.format("<CadetBlue>(%d%%)<reset>", math.floor(talisman.talismantotal*100/talisman.talismanrequired))) else cecho("<green>(100%)<reset>") end if talisman.setformat[set].value then talisman.credittotal = (talismanfull*talismandata.tradein) + ((talismancount - (talismanfull*4)) * talismandata.redeem) talisman.setcredittotal = (talisman.setcredittotal or 0) + (talisman.credittotal or 0) cecho(string.format("<red> %s %s %s <white>(%s %s)", talismanfull, talismancount, talisman.credittotal, (talismandata.tradein or 0), (talismandata.redeem or 0))) end cecho("\n") end
talisman.displaytalisman = function (talismanname, set, order) local temp = 0 local min = 99 local count = 0
if not talisman.info[talismanname] then talisman.echo(string.format("Invalid talisman for output %s", talismanname)) return end if not order then -- unsorted.. for k,d in pairs(talisman.info[talismanname]) do temp = talisman.displayitem(k, d, set) if temp < min then min = temp end count = count + temp end else for _,d in ipairs(order) do temp = talisman.displayitem(d, talisman.info[talismanname][d], set) if temp < min then min = temp end count = count + temp end end return min, count end
talisman.displayitem = function (name, data, set) count = talisman.getcount(name) if count>=data.required then cechoLink(string.format("<green>%s", talisman.rightpad(string.format("%s(%d)", string.cut(data.short,talisman.setformat[set].cellwidth), count), talisman.setformat[set].cellwidth+5)), [[printCmdLine("ttrade ]]..(target or "<person>").." "..name..[[")]], "Click to trade", true) talisman.talismantotal = talisman.talismantotal + data.required -- (dont include spares in our total required) talisman.talismanspare = talisman.talismanspare + count - data.required else cechoLink(string.format("<CadetBlue>%s", talisman.rightpad(string.format("%s(%d)", string.cut(data.short,talisman.setformat[set].cellwidth), count), talisman.setformat[set].cellwidth+5)), [[printCmdLine("ttrade ]]..(target or "<person>").." "..name..[[")]], "Click to trade", true) talisman.talismantotal = talisman.talismantotal + count -- (dont include spares in our total required) end talisman.talismanrequired = talisman.talismanrequired + data.required return math.floor(count/data.required) end
-- This just helps us deal with the fact that some pieces have a string thats to long (20 char limit on the talisman piece list).. talisman.createlookup = function () talisman.lookup ={} for i,v in pairs(talisman.sets) do for k,d in pairs(v) do for a,b in ipairs(d.order) do talisman.lookup[b:cut(20)] = b end end end end
talisman.createlookup()

