------------------------------------------------- -- Put your Lua functions here. -- -- -- -- Note that you can also use external Scripts -- ------------------------------------------------- function ignorecurebalance(bal)
if heldcures[bal] == 0 then
cecho("<green>Now pausing "..bal.." balance")
heldcures[bal] = 1 end
end
function enablecurebalance(bal)
if heldcures[bal] == 1 then cecho("<green>Now enabling "..bal.." balance")
heldcures[bal] = 0
end
end
function addignore(aff)
table.insert(cureignorelist, aff) cecho("\n<green>now ignoring "..aff)
end function removeignore(aff)
cureignorelist = listremove(cureignorelist, aff)
cecho("<green>now curing "..aff.." again")
end
