-- Function to inspect the contents of the quiver
function IQ()
    echo("\nInspecting Quiver:")
    echo("\nRegular Arrows (Undyed): " .. (quiver.arrows["undyed"] or 0))
    echo("\nMeteor Arrows: " .. (quiver.meteorArrows or 0))
    for color, amount in pairs(quiver.arrows) do
        if color ~= "undyed" then
            echo("\n"..color .. " arrows: " .. amount)
        end
    end
    -- Send INSPECT QUIVER command
    send("IQ")
end

function handleCombatAction()
balstring = ""
-- command separator local cs = cmdsep -- Initialize balstring to avoid nil errors if not table.contains(myaffs, "prone") then if requestpinshot then -- Handle pinshot action balstring = "get bow from backstrap" .. cs .. "wield bow" .. cs .. blockDecide() .. cs .. checkHypnosis() .. "pinshot " .. target .. cs .. cLightWall() else -- Handle hypnosis logic if hypnocount < hypnopointer and ((hypnostatus == "reset") or (hypnostatus == "locked")) and not (hypnostatus == "hypnotised") and not (hypnostatus == "snapped") then balstring = hypnomakestring() else -- Default combat action balstring = prefix() .. cs .. "wield shield fang" .. cs .. "wipe dirk" .. cs .. "dstab " .. target .. " " .. stackervenom .. " " .. carriervenom .. " " end end end end

-- Setup and Initialization function initializeDragonVariables() local balstring = "" local limb = limbrequested or "" local jinx = 0
local affcheck = { sensitivity = {claw = "prefarar", curse = "dragoncurse " .. target .. " sensitivity"}, paralysis = {claw = "curare", curse = "dragoncurse " .. target .. " paralysis"}, impatience = {curse = "dragoncurse " .. target .. " impatience"}, asthma = {claw = "kalmia"}, recklessness = {curse = "dragoncurse " .. target .. " recklessness", claw = "eurypteria"}, stupidity = {curse = "dragoncurse " .. target .. " stupidity", claw = "aconite"}, slickness = {claw = "gecko"}, anorexia = {claw = "slike"}, weariness = {claw = "vernalius"}, clumsiness = {claw = "xentio"} }
stacklist = stacklist or { {"paralysis", 1}, {"impatience", 1}, {"clumsiness", 0.5}, {"asthma", 0.5}, {"slickness", 0.5}, {"anorexia", 1} }
return balstring, limb, jinx, affcheck end

function GMCPParse(stat)
  --Because they sometimes pass goofy stuff
  --in the 'charstats' sub-table of vitals,
  --we need a way to parse that. Forrrrr example,
  --bleed. It gets passed as Bleed: # in charstats[1],
  --buuuut it might not ALWAYS be charstats[1], or
  --maybe it isn't there at all. Stupid. But here's
  --my solution.

  local stat = stat:title()..": "
  local toRet = false
  for _, v in pairs(gmcp.Char.Vitals.charstats) do
    if string.find(v, stat) then
      toRet = v:gsub(stat, "")
      toRet = toRet:gsub("%%", "")
      --sometimes we want the STRING, like morph: jaguar <-- want jaguar,
      --this will skip if there isn't a valid tonumber.
      if tonumber(toRet) then
        toRet = tonumber(toRet)
      end
    end
  end

  return toRet
end


function MonkStance()
monkstance = string.match(gmcp.Char.Vitals.charstats[4],"Stance\: (%w+)")
end

function getkai()
mykaii = tonumber(string.match(gmcp.Char.Vitals.charstats[3],"Kai\: (%d+)"))
end

