function getStance()
	for k, v in ipairs(gmcp.Char.Vitals.charstats) do
		if string.find(v, "Form") then stance = v:gsub("Form:%s", "") stance = stance:lower() end
	end
end
registerAnonymousEventHandler("gmcp.Char.Vitals", "getStance")

function getStance() for k, v in ipairs(gmcp.Char.Vitals.charstats) do if string.find(v, "Form") then shikudoForm = v:gsub("Form:%s", "") shikudoForm = stance:lower() end end end registerAnonymousEventHandler("gmcp.Char.Vitals", "getStance")
function shikudoBashing() shikudo_bashing = { willow = "combo "..target.." hiru hiraku flashheel right", rain = "combo "..target.." ruku torso kuro left frontkick left", oak = "combo "..target.." livestrike nervestrike risingkick head", tykonos = "combo "..target.." thrust head thrust head risingkick head" }
shikudo_nextform = { willow = "rain", rain = "oak", oak = "willow", tykonos = "willow" }
shikudo_katachange = { willow = 9, rain = 5, oak = 5, tykonos = 5 }
if not shikudo_bashing[shikudoForm] then send("adopt willow form") elseif kata >= shikudo_katachange[shikudoForm] then send("transition to "..shikudo_nextform[shikudoForm].." form") else send(shikudo_bashing[shikudoForm]) end
end
